def oneMillion = context.expand( '${GetQuoteOption#Response#$[0][\'quoteOptionId\']}' )

def twoMillion = context.expand( '${GetQuoteOption#Response#$[1][\'quoteOptionId\']}' )

def threeMillion = context.expand( '${GetQuoteOption#Response#$[2][\'quoteOptionId\']}' )

def fiveMillion = context.expand( '${GetQuoteOption#Response#$[3][\'quoteOptionId\']}' )

def oneMillionWithUMUIM  = context.expand( '${GetQuoteOption#Response#$[4][\'quoteOptionId\']}' )

def twoMillionWithLower = context.expand( '${GetQuoteOption#Response#$[5][\'quoteOptionId\']}' )

def threeMillionWithLower = context.expand( '${GetQuoteOption#Response#$[6][\'quoteOptionId\']}' )


def fiveMillionWithLower = context.expand( '${GetQuoteOption#Response#$[7][\'quoteOptionId\']}' )


def twoMillionWithMatching = context.expand( '${GetQuoteOption#Response#$[8][\'quoteOptionId\']}' )

def threeMillionWithMatching = context.expand( '${GetQuoteOption#Response#$[9][\'quoteOptionId\']}' )


def fiveMillionWithMatching = context.expand( '${GetQuoteOption#Response#$[10][\'quoteOptionId\']}' )

def premiumCoverage = context.expand( '${Data Source#PremiumCoverage}' )


log.info("Premiumcoverage: "+ premiumCoverage)


def uMUIM = context.expand( '${Data Source#UMUIM}' )

log.info("UMUIM coverage: " + uMUIM)


def state = context.expand( '${Data Source#State}' )

def quoteId = null;


if (state == "NM" && uMUIM == "Reject" )

{
	return quoteId = oneMillion

	}

else if (state == "NM" && uMUIM == "Purchase")
{
	return quoteId = twoMillion

	}

if(premiumCoverage == "1 Million"  && uMUIM == "No" || premiumCoverage == "1 Million"  && uMUIM == "Reject")
{
	return quoteId = oneMillion

	}
else if (premiumCoverage == "2 Million" && uMUIM == "No" || premiumCoverage == "2 Million"  && uMUIM == "Reject")

{

	return quoteId = twoMillion
	}

	else if (premiumCoverage == "3 Million" && uMUIM == "No" || premiumCoverage == "3 Million"  && uMUIM == "Reject")

{

	return quoteId = threeMillion
	}

	else if (premiumCoverage == "5 Million"  && uMUIM == "No" || premiumCoverage == "5 Million"  && uMUIM == "Reject")

{

	return quoteId = fiveMillion
	}

	else if (premiumCoverage == "1 Million" && uMUIM == "Yes/Lower" ||  premiumCoverage == "1 Million" && uMUIM == "Yes/Matching" || premiumCoverage == "1 Million"  && uMUIM == "Purchase" || premiumCoverage == "1 Million" && uMUIM == "Yes")

{

	return quoteId = oneMillionWithUMUIM
	}

		else if (premiumCoverage == "2 Million" && uMUIM == "Yes/Lower" || premiumCoverage == "2 Million"  && uMUIM == "Purchase" || premiumCoverage == "2 Million" && uMUIM == "Yes")

{

	return quoteId = twoMillionWithLower
	}
			else if (premiumCoverage == "3 Million" && uMUIM == "Yes/Lower" || premiumCoverage == "3 Million"  && uMUIM == "Purchase" || premiumCoverage == "3 Million" && uMUIM == "Yes")

{

	return quoteId = threeMillionWithLower
	}
			else if (premiumCoverage == "5 Million" && uMUIM == "Yes/Lower" || premiumCoverage == "5 Million"  && uMUIM == "Purchase" || premiumCoverage == "5 Million" && uMUIM == "Yes")

{

	return quoteId = fiveMillionWithLower
	}

		else if (premiumCoverage == "2 Million" && uMUIM == "Yes/Matching")

{

	return quoteId = twoMillionWithMatching
	}

			else if (premiumCoverage == "3 Million" && uMUIM == "Yes/Matching")

{

	return quoteId = threeMillionWithMatching
	}

			else if (premiumCoverage == "5 Million" && uMUIM == "Yes/Matching")

{

	return quoteId = fiveMillionWithMatching
	}



log.info ("QuoteId: "+ quoteId)

